// Actions are just javascript objects which takes key as type and payload
//remove posts
export function removePost(index) {
    return {
        type: 'REMOVE_POST',
        index:index
    }
}

export function addPost(post) {
    return {
        type: 'ADD_POST',
        post:post
    }
}

export function addComment(comment, postId) {
    return {
        type: 'ADD_COMMENT',
        comment:comment, 
        postId: postId
    }
}
