import posts from '../data/data';

const postReducer = function post(state = posts, action) {
    console.log(action.type);

    switch (action.type) {
        case 'REMOVE_PHOTO':
            return [...state.slice(0, action.index), ...state.slice(action.index + 1)]
        default:
            return state;
    }
}

export default postReducer;
